import React, { useState } from 'react';
import ToDoList from './ToDoList';
import './App.css';
import AddTaskBar from './AddTaskBar'
import { Task } from './App'
import { Container, Row, Col } from 'react-bootstrap';

const Home = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [index, setIndex] = useState(0);

  const addTask = (task: string) => {
    const newItems = [...tasks, {
      task,
      completed: false,
      index,
    }];

    setTasks(newItems);
    setIndex(index + 1);
  };

  function getDate() {
    var myCurrentDate = new Date();
    var date = myCurrentDate.getFullYear() + '-' + (myCurrentDate.getMonth() + 1) + '-' + myCurrentDate.getDate() + ' ' + myCurrentDate.getHours() + ':' + myCurrentDate.getMinutes() + ':' + myCurrentDate.getSeconds();
    const newCurrentDate = "Current Date and Time: " + date;
    return (
      <p>{newCurrentDate}</p>
    );
  }

  return (


    <Container className="homeContainer">
      <div className="home">
        <Row><Col><h1 className="plannerHeader">Planner of Name</h1></Col></Row>
        <p className="welcomeBack">
          âœ¨ Welcome Back Name! It is currently Monday.
        </p><getDate />

        <hr></hr>

        <ToDoList tasks={tasks} />
        <AddTaskBar addTask={addTask} />
      </div>
    </Container>)
    ;
}

export default Home
