import React, { useState } from 'react';
import type { Task } from './App'
import './App.css';
import AddTaskBar from './AddTaskBar'
import ToDoList from './AddTaskBar'

const DailyToDoList = () => {
  const [dailyTasks, setdailyTask] = useState<Task[]>([]);
  const [index, setIndex] = useState(0);

  const addTask = (dailyTask: string) => {
    const newItems = [...dailyTasks, {
      dailyTask,
      completed: false,
      index,
    }];

    setdailyTask(newItems);
    setIndex(index + 1);
  };

  return (
    <div>
      <ToDoList tasks={dailyTasks}></ToDoList>
      <AddTaskBar></AddTaskBar>
    </div>
  );
}

export default DailyToDoList
